';
            font-size: 3em;
            line-height: 0;
            vertical-align: -0.4em;
            color: #ddd;
        }

        blockquote {
            border-left: 4px solid #0066cc;
            padding-left: 20px;
            margin: 30px 0;
            font-style: italic;
            color: #666;
        }

        a {
            color: #0066cc;
            text-decoration: none;
            border-bottom: 1px solid transparent;
            transition: border-color 0.2s;
        }

        a:hover {
            border-bottom-color: #0066cc;
        }

        .drop-cap {
            float: left;
            font-size: 4em;
            line-height: 0.8;
            margin-right: 8px;
            margin-top: 4px;
            font-weight: 700;
            color: #1a1a1a;
        }

        figure {
            margin: 40px 0;
            text-align: center;
        }

        figure img {
            max-width: 100%;
            height: auto;
            border-radius: 4px;
        }

        figcaption {
            font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
